{
    /**
     * 是否需要将参数反映在地址的查询参数中
     */
    inSearch?: boolean;
    /**
     * 下载的文件名
     *
     * @defaultValue 'download'
     */
    filename?: string;
    /**
     * 构建查询参数组件
     *
     * 可以为空，可能存在只有分页的情况。
     *
     * @reactive
     */
    queryForm?: {
        (oa: ObjectAccessor<Q>): JSX.Element;
    };
    /**
     * 查询参数的默认值
     *
     * 如果没有查询参数可以使用 `{}` 代替。
     *
     * NOTE: 如果 {@link Props#inSearch} 为 true，那么地址中的参数将覆盖此参数中的相同名称的字段。
     *
     * @reactive
     */
    queries: Q;
    /**
     * 工具栏的内容
     *
     * 此属性提供的内容显示在左侧部分。工具栏右侧部分为框架自身提供的功能。
     *
     * @reactive
     */
    toolbar?: JSX.Element;
    /**
     * 是否需要展示框架自身提供的工具栏功能
     *
     * @reactive
     */
    systemToolbar?: boolean;
    /**
     * 一些突出元素的主题色，默认值为 primary。
     *
     * @reactive
     * @defaultValue 'primary'
     */
    accentPalette?: Palette;
}

{
    /**
     * 加载数据的方法
     */
    load: {
        (q: Q): Promise<Page_2<T> | undefined>;
    };
    /**
     * 数据是否分页展示
     */
    paging: true;
    /**
     * 可用的每页展示数量
     *
     * NOTE: 只有在 paging 为 true 时才会有效
     */
    pageSizes?: Array<number>;
    /**
     * 每一页的数量
     *
     * @reactive
     * @defaultValue `Options.pageSize`
     */
    pageSize?: number;
}

