/**
 * 定义主题相关的各类变量
 */
export declare type Scheme = {
    primary: string;
    secondary: string;
    tertiary: string;
    /**
     * 表示错误信息
     */
    error: string;
    /**
     * 一般用于大面积的背景色
     */
    surface: string;
    /**
     * 各种不同大小的组件的圆角设置
     */
    radius: Radius;
}

/**
 * 圆角参数的设置
 *
 * @remarks
 * 属性名表示的是组件的大小。单位为 rem。
 */
export declare type Radius = {
    xs: number;
    sm: number;
    md: number;
    lg: number;
    xl: number;
}

/**
 * 接口错误返回的对象
 *
 * @typeParam E - 表示 {@link extension} 字段的类型，如果该字段空值，不需要指定。
 */
export declare interface Problem<E = never> {
    type: string;
    title: string;
    status: number;
    detail?: string;
    instance?: string;
    extension?: E;
    headers?: Headers;
    /**
     * 具体的错误字段
     *
     * 根据 {@link status} 的不同，可能表示提交对象、查询参数或是报头的错误。
     */
    params?: Params;
}

/**
 * 验证器
 *
 * @typeParam T - 需要验证的数据类型；
 */
export declare interface Validator<T extends Flattenable> {
    /**
     * 改变当前语言
     *
     * @remarks
     * 该操作会改变之后对数据验证时的错误信息
     */
    changeLocale(locale: Locale): void;
    /**
     * 验证数据
     *
     * @param obj - 需要验证的数据；
     * @param path - 如果不为空表示采用规则 path 验证 obj；
     */
    valid(obj: any, path?: FlattenKeys<T>): Promise<ValidResult<T>>;
}

/**
 * 表示与后端交互过程中的错误信息
 */
export declare class APIError extends Error {
    #private;
    /**
     * 将 {@link Problem} 转换为 {@link APIError} 对象
     */
    static fromProblem<E>(p: Problem<E>): APIError;
    /**
     * 构造函数
     *
     * @param status - 状态码；
     * @param title - 简要的错误说明；
     * @param headers - HTTP 响应头；
     * @param message - 详细的错误说明；
     */
    constructor(status: number, title?: string, headers?: Headers, message?: string);
    /**
     * 表示 HTTP 状态码
     */
    get status(): number;
    /**
     * 表示简要的错误说明
     */
    get title(): string | undefined;
    /**
     * 报头
     */
    get headers(): Headers | undefined;
}
